const KEY = 'b5ebc8e4';
// http://www.omdbapi.com/?i=tt3896198&apikey=b5ebc8e4

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [query, setQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedId, setSelectedId] = useState(null);

  function handleSelectMovie(id) {
    setSelectedId(
      selectedId => id === selectedId ? null : id
    );
  }

  function handleCloseMovie() {
    setSelectedId(null);
  }

  function handleAddWatched(movie) {
    setWatched(watched => ([...watched, movie]));
  }

  useEffect(() => {

    async function fetchData() {
      try {
        setIsLoading(true);
        setError('');
        const response = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&s=${query}`);
        const data = await response.json();
        setMovies(data.Search);
        setIsLoading(false);
      }
      catch (error) {
        console.log(error);
        setError('something went wrong');
        setMovies([]);
      }
    }
    // fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=${KEY}&s=2012`)
    //   .then(
    //     response => response.json()
    //   )
    //   .then(
    //     (data) => setMovies(data.Search)
    //   )
    if (!query.length) {
      setMovies([]);
      setError('');
      return
    }
    fetchData();
  }, [query]
  )


  return (
    <>
      <NavBar>
        <Logo />
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>
      <Main>
        {/* <ListBox>
          <MovieList movies={movies} />
        </ListBox>
        <WatchedBox>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
  </WatchedBox>*/}
        <Box>
          {
            isLoading ? <Loader /> : error ? error : movies ? <MovieList onSelectMovie={handleSelectMovie} movies={movies} /> : 'no result'
          }
        </Box>
        <Box>
          {
            selectedId ? (<MovieDetails onClose={handleCloseMovie} onAddWatched={handleAddWatched} selectedId={selectedId} />) : (<>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList watched={watched} /
              ></>)
          }
        </Box>
      </Main>
    </>
  );
}

function Loader() {
  return (
    <p className="loader">Loading...</p>
  )
}

function MovieDetails({ selectedId, onClose, onAddWatched, watched }) {
  const [movie, setMovie] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState('');
  const isWatched = watched.map(movie => movie.imdbID).includes(selectedId);

  const {
    Title: title,
    Poster: poster,
    Runtime: runtime,
    imdbRating,
    Plot: plot,
    Released: released,
    Actors: actors,
    Director: director,
    Genre: genre,
  } = movie;

  function handleAdd() {
    const newWatchedMovie = {
      imdbID: selectedId,
      title,
      poster,
      imdbRating: Number(imdbRating),
      runtime: Number(runtime.split(' ').at(0)),
      userRating
    }
    onAddWatched(newWatchedMovie)
    onClose();
  }

  useEffect(
    () => {
      async function getMovieDetails() {
        setIsLoading(true);
        const response = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&i=${selectedId}`);
        const data = await response.json();
        setMovie(data);
        setIsLoading(false)
      }
      getMovieDetails();
    },
    [selectedId])
  return (
    <div className="details">
      {
        isLoading ? <Loader /> :
          <>
            <header>
              <button className="btn-back" onClick={onClose}>&larr;</button>
              <img src={poster} alt={`Poster of ${movie}`} />
              <div className="details-overview">
                <h2>{title}</h2>
                <p>
                  {released} &bull; {runtime}
                </p>
                <p>{genre}</p>
                <p>
                  <span>⭐</span>
                  {imdbRating} IMDb rating
                </p>
              </div>
            </header>
            <section>
              <div className="rating">
                <StarRating maxRating={10} size={24} onSetRating={setUserRating} />
                {
                  userRating > 0 && <button className="btn-add" onClick={handleAdd}>+ Add to list</button>
                }
              </div>
              <p>
                <em>{plot}</em>
              </p>
              <p>Starring: {actors} </p>
              <p>Directed by {director} </p>
            </section>
          </>
      }


    </div>
  )
}
