import { useState, useEffect } from "react";

export default function useKey(onClose) {
    useEffect(
        () => {
            function callback(e) {
                if (e.code === 'Escape') {
                    onClose();
                }
            }

            document.addEventListener('keydown', callback)
            return (
                () => {
                    document.removeEventListener('keydown', callback);
                }
            )
        }
        , [onClose]
    )
    return onClose;
}
