export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [query, setQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedId, setSelectedId] = useState(null);

  function handleSelectMovie(id) {
    setSelectedId(
      selectedId => id === selectedId ? null : id
    );
  }

  function handleCloseMovie() {
    setSelectedId(null);
  }

  useEffect(() => {

    async function fetchData() {
      try {
        setIsLoading(true);
        setError('');
        const response = await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=${KEY}&s=${query}`);
        const data = await response.json();
        setMovies(data.Search);
        setIsLoading(false);
      }
      catch (error) {
        console.log(error);
        setError('something went wrong');
        setMovies([]);
      }
    }
    // fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=${KEY}&s=2012`)
    //   .then(
    //     response => response.json()
    //   )
    //   .then(
    //     (data) => setMovies(data.Search)
    //   )
    if (!query.length) {
      setMovies([]);
      setError('');
      return
    }
    fetchData();
  }, [query]
  )


  return (
    <>
      <NavBar>
        <Logo />
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>
      <Main>
        {/* <ListBox>
          <MovieList movies={movies} />
        </ListBox>
        <WatchedBox>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
  </WatchedBox>*/}
        <Box>
          {
            isLoading ? <Loader /> : error ? error : movies ? <MovieList onSelectMovie={handleSelectMovie} movies={movies} /> : 'no result'
          }
        </Box>
        <Box>
          {
            selectedId ? (<MovieDetails onClose={handleCloseMovie} selectedId={selectedId} />) : (<>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList watched={watched} /
              ></>)
          }
        </Box>
      </Main>
    </>
  );
}

