import { useState, useEffect } from "react";

export default function useKey(onPress, usedKey) {
    useEffect(
        () => {
            function callback(e) {
                if (e.code === usedKey) {
                    onPress();
                }
            }

            document.addEventListener('keydown', callback)
            return (
                () => {
                    document.removeEventListener('keydown', callback);
                }
            )
        }
        , [onPress, key]
    )
}
